import { Router } from 'express';
import config from '../../../config'; // Asegrate de que esta ruta sea vlida
import prestamoRoutes from './prestamo.routes'; // Rutas especficas para prstamos
import clienteRoutes from './cliente.routes'; // Rutas especficas para clientes

const routerAPI = (app) => {
    const router = Router();
    const api = config.API_URL; // '/api/v1'

    app.use(api, router);

    // Rutas
    router.use('/prestamos', prestamoRoutes); // Se asocia al prefijo '/api/v1/prestamos'
    router.use('/clientes', clienteRoutes); // Se asocia al prefijo '/api/v1/clientes'

    return router;
};

module.exports = routerAPI;

