sourcesContent:
import Cliente from '../models/cliente';

class ClienteService {
    async listAll() {
        return await Cliente.find({}).populate('prestamosActivos historialPrestamos');
    }

    async findById(clienteId) {
        return await Cliente.findById(clienteId).populate('prestamosActivos historialPrestamos');
    }

    async create(data) {
        const cliente = new Cliente(data);
        return await cliente.save();
    }

    async update(clienteId, data) {
        return await Cliente.findByIdAndUpdate(clienteId, data, { new: true });
    }

    async delete(clienteId) {
        return await Cliente.findByIdAndDelete(clienteId);
    }
}

export default new ClienteService();

