import Prestamo from '../models/Prestamo';

class PrestamoService {
    async listAll() {
        return await Prestamo.find({}).populate('clienteId');
    }

    async findById(prestamoId) {
        return await Prestamo.findById(prestamoId).populate('clienteId');
    }

    async create(data) {
        const prestamo = new Prestamo(data);
        return await prestamo.save();
    }

    async update(prestamoId, data) {
        return await Prestamo.findByIdAndUpdate(prestamoId, data, { new: true });
    }

    async delete(prestamoId) {
        return await Prestamo.findByIdAndDelete(prestamoId);
    }
}

export default new PrestamoService();

