import mongoose from 'mongoose';

const prestamoSchema = new mongoose.Schema({
    clienteId: { type: mongoose.Schema.Types.ObjectId, ref: 'Cliente', required: true },
    tipo: { type: String, enum: ['semanal', 'mensual'], required: true },
    montoPrestado: { type: Number, required: true },
    tasaInteres: { type: Number, default: 0.1 },
    montoInteres: { type: Number },
    montoTotal: { type: Number },
    saldoRestante: { type: Number },
    pagos: [
        {
            numeroPago: { type: Number },
            fechaPago: { type: Date },
            monto: { type: Number },
            restante: { type: Number }
        }
    ],
    totalPagos: { type: Number },
    pagosRealizados: { type: Number, default: 0 },
    fechaInicio: { type: Date, default: Date.now },
    fechaTermino: { type: Date, default: null },
    status: { type: String, enum: ['activo', 'liquidado', 'cancelado'], default: 'activo' }
});

export default mongoose.model('Prestamo', prestamoSchema, 'prestamos');

