import prestamoService from '../services/prestamo.service';
import clienteService from '../services/cliente.service ';

export const getAllPrestamos = async (req, res) => {
    try {
        const prestamos = await prestamoService.listAll();
        res.status(200).json(prestamos);
    } catch (error) {
        console.error('Error en getAllPrestamos:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};

export const getPrestamoById = async (req, res) => {
    try {
        const { id } = req.params;
        const prestamo = await prestamoService.findById(Number(id));
        if (!prestamo) {
            return res.status(404).json({ message: 'Prstamo no encontrado' });
        }
        res.status(200).json(prestamo);
    } catch (error) {
        console.error('Error en getPrestamoById:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};

export const createPrestamo = async (req, res) => {
    try {
        const { clienteId } = req.body;

        // Verificar que el cliente exista por su `id`
        const cliente = await clienteService.findById(Number(clienteId));
        if (!cliente) {
            return res.status(404).json({ message: 'Cliente no encontrado' });
        }

        // Crear el prstamo
        const prestamo = await prestamoService.create(req.body);

        // Agregar el `id` del prstamo al campo prestamosActivos del cliente
        cliente.prestamosActivos.push(prestamo.id);
        await clienteService.update(cliente.id, cliente);

        res.status(201).json(prestamo);
    } catch (error) {
        console.error('Error en createPrestamo:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};

export const updatePrestamo = async (req, res) => {
    try {
        const { id } = req.params;
        const updatedPrestamo = await prestamoService.update(Number(id), req.body);
        if (!updatedPrestamo) {
            return res.status(404).json({ message: 'Prstamo no encontrado' });
        }
        res.status(200).json(updatedPrestamo);
    } catch (error) {
        console.error('Error en updatePrestamo:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};

export const deletePrestamo = async (req, res) => {
    try {
        const id = Number(req.params.id); // Convertir el parmetro `id` a nmero

        if (isNaN(id)) {
            return res.status(400).json({ message: 'El ID proporcionado no es vlido' });
        }

        // Buscar el prstamo por `id`
        const prestamo = await prestamoService.findById(id);
        if (!prestamo) {
            return res.status(404).json({ message: 'Prstamo no encontrado' });
        }

        // Buscar el cliente asociado
        const cliente = await clienteService.findById(prestamo.clienteId);
        if (cliente) {
            // Eliminar el prstamo de la lista de `prestamosActivos` o `historialPrestamos`
            cliente.prestamosActivos = cliente.prestamosActivos.filter(
                (prestamoId) => prestamoId !== id
            );

            // Guardar los cambios en el cliente
            await clienteService.update(cliente.id, cliente);
        }

        // Eliminar el prstamo
        await prestamoService.delete(id);

        res.status(200).json({ message: 'Prstamo eliminado correctamente' });
    } catch (error) {
        console.error('Error en deletePrestamo:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};

export const addPago = async (req, res) => {
    try {
        const { id } = req.params; // ID del prstamo
        const { monto, fecha } = req.body; // Detalles del pago

        // Buscar el prstamo por ID
        const prestamo = await prestamoService.findById(Number(id));
        if (!prestamo) {
            return res.status(404).json({ message: 'Prstamo no encontrado' });
        }

        // Verificar si el prstamo ya est liquidado
        if (prestamo.status === 'liquidado') {
            return res.status(400).json({ message: 'No se puede agregar un pago a un prstamo liquidado' });
        }

        // Generar el folio del pago (incremental)
        const folio = prestamo.pagos.length + 1;

        // Crear el pago
        const nuevoPago = {
            folio,
            monto,
            fecha,
        };

        // Actualizar el prstamo
        prestamo.pagos.push(nuevoPago);
        prestamo.saldoRestante -= monto;
        prestamo.pagosRealizados += 1;

        // Si el saldo restante llega a 0, marcar el prstamo como "liquidado"
        if (prestamo.saldoRestante === 0) {
            prestamo.status = 'liquidado';
            prestamo.fechaTermino = new Date(); // Registrar la fecha de liquidacin
        }

        // Guardar cambios
        const prestamoActualizado = await prestamoService.update(prestamo.id, prestamo);

        res.status(200).json({
            message: 'Pago agregado exitosamente',
            prestamo: prestamoActualizado,
        });
    } catch (error) {
        console.error('Error en addPago:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};


export const deletePago = async (req, res) => {
    try {
        const { id, folio } = req.params; // ID del prstamo y folio del pago

        // Buscar el prstamo por ID
        const prestamo = await prestamoService.findById(Number(id));
        if (!prestamo) {
            return res.status(404).json({ message: 'Prstamo no encontrado' });
        }

        // Buscar el pago por folio dentro del arreglo de pagos
        const pagoIndex = prestamo.pagos.findIndex((p) => p.folio === Number(folio));
        if (pagoIndex === -1) {
            return res.status(404).json({ message: 'Pago no encontrado' });
        }

        // Obtener el monto del pago que se eliminar
        const montoEliminado = prestamo.pagos[pagoIndex].monto;

        // Eliminar el pago del arreglo de pagos
        prestamo.pagos.splice(pagoIndex, 1);

        // Recalcular los valores del prstamo
        prestamo.saldoRestante += montoEliminado; // Incrementar el saldo restante
        prestamo.pagosRealizados -= 1;

        // Si el saldo restante ya no es 0, cambiar el estado del prstamo
        if (prestamo.saldoRestante > 0 && prestamo.status === 'liquidado') {
            prestamo.status = 'activo';
            prestamo.fechaTermino = null;
        }

        // Guardar los cambios en el prstamo
        const prestamoActualizado = await prestamoService.update(prestamo.id, prestamo);

        res.status(200).json({
            message: 'Pago eliminado exitosamente',
            prestamo: prestamoActualizado,
        });
    } catch (error) {
        console.error('Error en deletePago:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};




export const editPago = async (req, res) => {
    try {
        const { id, folio } = req.params; // ID del prstamo y folio del pago
        const { monto, fecha } = req.body; // Nuevos datos del pago

        // Buscar el prstamo por ID
        const prestamo = await prestamoService.findById(Number(id));
        if (!prestamo) {
            return res.status(404).json({ message: 'Prstamo no encontrado' });
        }

        // Buscar el pago por folio dentro del arreglo de pagos
        const pagoIndex = prestamo.pagos.findIndex((p) => p.folio === Number(folio));
        if (pagoIndex === -1) {
            return res.status(404).json({ message: 'Pago no encontrado' });
        }

        // Obtener el monto anterior
        const montoAnterior = prestamo.pagos[pagoIndex].monto;

        // Actualizar los datos del pago
        prestamo.pagos[pagoIndex].monto = monto;
        prestamo.pagos[pagoIndex].fecha = fecha;

        // Ajustar el saldo restante segn el nuevo monto
        if (monto > montoAnterior) {
            prestamo.saldoRestante -= (monto - montoAnterior); // Disminuir saldo restante
        } else if (monto < montoAnterior) {
            prestamo.saldoRestante += (montoAnterior - monto); // Aumentar saldo restante
        }

        // Si el saldo restante llega a 0, marcar el prstamo como "liquidado"
        if (prestamo.saldoRestante === 0) {
            prestamo.status = 'liquidado';
            prestamo.fechaTermino = new Date();
        } else if (prestamo.status === 'liquidado' && prestamo.saldoRestante > 0) {
            // Cambiar el estado a "activo" si el saldo restante ya no es 0
            prestamo.status = 'activo';
            prestamo.fechaTermino = null;
        }

        // Guardar los cambios en el prstamo
        const prestamoActualizado = await prestamoService.update(prestamo.id, prestamo);

        res.status(200).json({
            message: 'Pago editado exitosamente',
            prestamo: prestamoActualizado,
        });
    } catch (error) {
        console.error('Error en editPago:', error);
        res.status(500).json({ message: 'Error interno del servidor' });
    }
};

