import jwt from "jsonwebtoken";
import User from "../models/User.js";

export const authMiddleware = async (req, res, next) => {
    const authHeader = req.headers.authorization;

    if (!authHeader) {
        return res.status(401).json({ message: "No se proporcion un token" });
    }

    const token = authHeader.split(" ")[1];

    try {
        const decoded = jwt.verify(token, process.env.JWT_SECRET);

        // Buscar al usuario en la base de datos
        const user = await User.findById(decoded.userId);
        if (!user) {
            return res.status(404).json({ message: "Usuario no encontrado" });
        }

        // Verificar si el token fue emitido antes del ltimo cierre de sesin
        if (user.lastLogout && new Date(decoded.iat * 1000) < user.lastLogout) {
            return res.status(401).json({ message: "Token invlido o expirado" });
        }

        req.user = decoded;
        next();
    } catch (error) {
        return res.status(401).json({ message: "Token invlido" });
    }
};
